import { useNavigate } from "react-router";
import { Link } from "react-router-dom";
import axios from "axios";


const Buttons = () => {

    //NECESITO USER ACA / BAJAR DE REDUX
    const user = {}; //este user es temporal / borrar
    const navigate = useNavigate();
    const handleLogout = (e) => {
      axios.post("/api/logout").then((res) => setUser({})); //R
      navigate("/home");                            //R
    };


    //REVISAR RUTAS LOGIN / REGISTER
    return user.id ? (
      <div className="dropdown userIcon">
       <i className="fa-solid fa-child fa-lg"></i>
        <div>
          <h6 className="text-white userS">{user.userName}</h6>
        </div>
        <div className="dropdown-content">
          <a href="/favorites">Favorites</a>
          <a href="/upcomingMovies" onClick={handleLogout}>
            LogOut
          </a>
        </div>
      </div>
    ) : (
      <>
        <Link to={"user/login"}> 
          <button className="btn botonColorNav">Login</button>
        </Link>

        <Link to={"user/register"}>
          <button className="btn botonColorNav">Register</button>
        </Link>
      </>
    );
  };

  export default Buttons;
